''''3' / 4 # TypeError'10' / '2'# TypeErrorint('7') / 7.0"阿古兽进化...暴龙兽"[11] # IndexErrorxyz = 12; xyZ * 3 # name Error'''def clean_text(text1, text2):    '''    返回合并后的str        text1 -- str，包含订单中的客户信息    text2 -- str，包含订单中金额信息    '''    # 字母    letter = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'    number = '0123456789'        try:        if not (isinstance(text1, str) and isinstance(text2, str)):            raise TypeError                # 获取名字        name:str = ""        for l in text1:            if l in letter:                name += l        # 排除名字为空        if l == "":            name = "empty"        name = name.title()                # 获取金额        money = ""        for m in text2:            if m in number:                money += m        money = "$" + money                    print(f"订单客户名：{name}, 订单金额{money}")    except TypeError:        print("你输入的不是string")        clean_text(2,1314)clean_text("&*@!(^&*(6ghdjkasgdkj","adhsjkh1234879yihk")        