def unique(text:tuple):    '''        Parameters    ----------    text : tuple        DESCRIPTION.    Returns    -------    bool        return t or f depending on if there are repeated letter.    '''    assert isinstance(text,str), "请输入string"    textset = set(text)    if len(textset) == len(text):        return True    else:        return False        def cut_tuple(a:tuple):    return a[::3]